import { useState } from "react";

const initialFriends = [
  {
    id: 118836,
    name: "Clark",
    image: "https://i.pravatar.cc/48?u=118836",
    balance: -7,
  },
  {
    id: 933372,
    name: "Sarah",
    image: "https://i.pravatar.cc/48?u=933372",
    balance: 20,
  },
  {
    id: 499476,
    name: "Anthony",
    image: "https://i.pravatar.cc/48?u=499476",
    balance: 0,
  },
];

function Button({ children, handlleToggle }) {
  return (
    <button className="button" onClick={handlleToggle}>{children}</button>
  )
}

function Friend({ friend }) {
  return (
    <li>
      <img src={friend.image} alt={friend.name} />
      <h3>{friend.name}</h3>
      {
        friend.balance < 0 ?
          <p className="red">You owe{friend.name} {Math.abs(friend.balance)}$</p> :
          friend.balance > 0 ?
            <p className="green">{friend.name} owes you {friend.balance}</p> :
            <p>{friend.name} and you are even</p>
      }
      <Button>Select</Button>
    </li>
  )
}

function FriendsList({ data }) {
  return (
    <ul>
      {
        data.map(i => <Friend friend={i} key={i.id} />)
      }
    </ul>
  )
}



function FormAddFriend() {
  return (
    <form className="form-add-friend">
      <label>ðŸŒ¸Friend Name</label>
      <input type="text" />
      <label>ðŸ”—Image URL</label>
      <input type="text" />

      <Button>Add</Button>
    </form>
  )
}

function FormSplitBill() {
  return (
    <form className="form-split-bill">
      <h2>Split a bill with PlaceHolder</h2>
      <label>ðŸ’°Total Cost </label>
      <input type="text" />
      <label>ðŸ’°Your expense</label>
      <input type="text" />
      <label>ðŸ’° X's expense</label>
      <input type="text" disabled />
      <label>ðŸ’°  Who's paying the bill</label>
      <select>
        <option value="user" key="">You</option>
        <option value="friend" key="">X</option>
      </select>
      <Button>Split Bill</Button>
    </form>
  )
}

export default function App() {
  const [isOpen, setIsOpen] = useState(false)
  function handlleToggle() {
    setIsOpen(!isOpen);
  }

  return (
    <div className="app">
      <div className="sidebar" >
        <FriendsList data={initialFriends} />
        <FormAddFriend />
        {
          isOpen ? <Button handlleToggle={handlleToggle}>Close</Button> : <Button handlleToggle={handlleToggle}>Add Friend</Button>
        }

      </div>
      <FormSplitBill />
    </div>
  )
}
