import { useState } from "react";

const initialFriends = [
  {
    id: 118836,
    name: "Clark",
    image: "https://i.pravatar.cc/48?u=118836",
    balance: -7,
  },
  {
    id: 933372,
    name: "Sarah",
    image: "https://i.pravatar.cc/48?u=933372",
    balance: 20,
  },
  {
    id: 499476,
    name: "Anthony",
    image: "https://i.pravatar.cc/48?u=499476",
    balance: 0,
  },
];

function Button({ children, onClick }) {
  return (
    <button className="button" onClick={onClick}>{children}</button>
  )
}

function Friend({ friend }) {
  return (
    <li>
      <img src={friend.image} alt={friend.name} />
      <h3>{friend.name}</h3>
      {
        friend.balance < 0 ?
          <p className="red">You owe{friend.name} {Math.abs(friend.balance)}$</p> :
          friend.balance > 0 ?
            <p className="green">{friend.name} owes you {friend.balance}</p> :
            <p>{friend.name} and you are even</p>
      }
      <Button>Select</Button>
    </li>
  )
}

function FriendsList({ data }) {
  return (
    <ul>
      {
        data.map(i => <Friend friend={i} key={i.id} />)
      }
    </ul>
  )
}



function FormAddFriend({ isOpen }) {
  const [name, setName] = useState('');
  const [image, setImage] = useState('https://i.pravatar.cc/48');

  const id = crypto.randomUUID();
  function handleSubmit(e) {
    e.preventDefault();
    if (!name || !image) return;
    const newFriend = {
      name,
      image: `${image}?=${id}`,
      balance: 0,
      id
    }
    setName('');
    setImage('https://i.pravatar.cc/48');
  }

  return (
    isOpen && (<form className="form-add-friend" onSubmit={handleSubmit}>
      <label label >🌸Friend Name</label >
      <input type="text" value={name} onChange={e => setName(e.target.value)} />
      <label>🔗Image URL</label>
      <input type="text" value={image} onChange={e => setImage(e.target.value)} />
      <Button>Add</Button>
    </form >)
  )
}

function FormSplitBill() {
  return (
    <form className="form-split-bill">
      <h2>Split a bill with PlaceHolder</h2>
      <label>💰Total Cost </label>
      <input type="text" />
      <label>💰Your expense</label>
      <input type="text" />
      <label>💰 X's expense</label>
      <input type="text" disabled />
      <label>💰  Who's paying the bill</label>
      <select>
        <option value="user" key="">You</option>
        <option value="friend" key="">X</option>
      </select>
      <Button>Split Bill</Button>
    </form>
  )
}

export default function App() {
  const [isOpen, setIsOpen] = useState(false);
  const [mateList, setMateList] = useState(initialFriends);

  function handlleToggle() {
    setIsOpen(!isOpen);
  }



  return (
    <div className="app">
      <div className="sidebar" >
        <FriendsList data={mateList} />
        <FormAddFriend isOpen={isOpen} />
        {
          isOpen ? <Button onClick={handlleToggle}>Close</Button> : <Button onClick={handlleToggle}>Add Friend</Button>
        }
      </div>
      <FormSplitBill />
    </div>
  )
}
